
var INGObjectDetails = {

	/**
	 * @enum {module, decorator, constant, value, filter, directive, provider, service, factory, controller, animation, config, run}
	 */
	"type": '',

	/**
	 * Name of the type generated by the constructor
	 * @type {String}
	 */
	"name": '',

	/**
	 * @type {String}
	 */
	"module": '',

	/**
	 * List of all dependencies for the given type
	 * @type {String[]}
	 */
	"dependencies": []
};

/**
 * Container for angular module object
 * @param {String} module
 * @param {Enum} type
 * @param {String} name
 * @param {String[]} dependencies
 * @constructor
 */
var NGObjectDetails = function(module, type, name, dependencies) {
	this.module = module;
	this.type = type;
	this.name = name;
	this.dependencies = dependencies;
};

module.exports = NGObjectDetails;
